
public class SubstringString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	String str = "Hello world!";
	
	System.out.println(str);
	
	System.out.println(str.substring(2)); //llo world! (start from 2nd index to end)
	
	System.out.println(str.substring(6, 9)); //wor (start from 6th index to 9th index excluding the letter of 9th index)
	
	
	}

}
